from ib_users.exceptions.custom_exception_constants import \
    REFRESH_TOKEN_EXPIRED, REFRESH_TOKEN_NOT_FOUND, ACCESS_TOKEN_NOT_FOUND, \
    CLIENT_NOT_FOUND
from ib_users.validators.base_validator import CustomException


class RefreshTokenNotFound(CustomException):
    def __init__(self):
        super(RefreshTokenNotFound, self).__init__(
            message=REFRESH_TOKEN_NOT_FOUND.message,
            exception_type=REFRESH_TOKEN_NOT_FOUND.code
        )

        self.error_type = REFRESH_TOKEN_NOT_FOUND.code
        self.error_message = REFRESH_TOKEN_NOT_FOUND.message


class RefreshTokenExpired(CustomException):
    def __init__(self):
        super(RefreshTokenExpired, self).__init__(
            message=REFRESH_TOKEN_EXPIRED.message,
            exception_type=REFRESH_TOKEN_EXPIRED.code
        )

        self.error_type = REFRESH_TOKEN_EXPIRED.code
        self.error_message = REFRESH_TOKEN_EXPIRED.message


class AccessTokenNotFoundException(CustomException):
    def __init__(self):
        super(AccessTokenNotFoundException, self).__init__(
            message=ACCESS_TOKEN_NOT_FOUND.message,
            exception_type=ACCESS_TOKEN_NOT_FOUND.code
        )

        self.error_type = ACCESS_TOKEN_NOT_FOUND.code
        self.error_message = ACCESS_TOKEN_NOT_FOUND.message


class InvalidClient(CustomException):
    def __init__(self):
        super(InvalidClient, self).__init__(
            message=CLIENT_NOT_FOUND.message,
            exception_type=CLIENT_NOT_FOUND.code
        )

        self.error_type = CLIENT_NOT_FOUND.code
        self.error_message = CLIENT_NOT_FOUND.message
