import abc

from ib_users.utils.dtos import PhoneNumberDTO


class SMSSender:
    __metaclass__ = abc.ABCMeta

    @classmethod
    @abc.abstractmethod
    def send_otp(cls, country_code: str, phone_number: str, otp: str):
        pass

    @abc.abstractmethod
    def send_reset_password_link(self, phone_number_dto: PhoneNumberDTO,
                                 link: str, dlt_template_id: str):
        pass
