import abc


class SocialAccount:
    __metaclass__ = abc.ABCMeta

    @abc.abstractmethod
    def get_social_id(self, **kwargs) -> str:
        pass

    @abc.abstractmethod
    def get_social_account_type(self) -> str:
        pass

    # TODO [3rd party] why do we need this ?
    @abc.abstractmethod
    def check_and_throw_exception_if_given_string_is_empty(self, string: str):
        pass

    @abc.abstractmethod
    def get_user_name_from_social_account(self, **kwargs) -> str:
        pass

    @abc.abstractmethod
    def get_email_from_social_account(self, **kwargs) -> str:
        pass
