from ib_users.interactors.presenters.user_account_presenter import \
    UserAccountPresenter
from ib_users.interactors.storages.user_accounts_storage import UserAccountDTO


class UserAccountJSONPresenter(UserAccountPresenter):

    def get_user_account(self, user_account_dto: UserAccountDTO):
        return {
            "user_id": str(user_account_dto.user_id),
            "username": user_account_dto.username
        }
