        from ib_users.storages import UserAccountsSQLStorage
        user_accounts_sql_storage = UserAccountsSQLStorage()

        user = UserAccountCreationForTestCases.create_user_with_username(
            username="username",
            password="password@#$",
            is_active=is_user_active)
