@pytest.mark.django_db
@pytest.mark.parametrize("username,phone_number,country_code,email",[
    ("example", "", "", ""), ("v", "", "", "example@gmail.com"),
    ("example", "9866546440", "+91", ""),
    ("example", "9866546440", "+91", "example@gmail.com")])
def test_get_user_details_to_send_otp_given_username(
        username, phone_number, country_code, email):
    user_account = UserAccount(
        username=username, email=email, phone_number=phone_number,
        country_code=country_code)
    user_account.save()

    user_accounts_storage_impl = UserAccountsSQLStorage()
    contact_details = user_accounts_storage_impl.get_user_details_to_send_otp_given_username(
        username)
    assert contact_details.email == email
    assert contact_details.phone_number == PhoneNumberDTO(phone_number,
                                                          country_code)
    assert contact_details.user_id == user_account.user_id


@pytest.mark.django_db
def test_get_user_ids_for_given_phone_numbers():
    from ib_users.interactors.DTOs.common_dtos import PhoneNumberDTO
    from ib_users.interactors.storages.user_accounts_storage import UserIdWithPhoneNumberDTO

    phone_number_with_country_code_dtos = [
        PhoneNumberDTO(
            phone_number="1234567890",
            country_code="91"),
        PhoneNumberDTO(
            phone_number="0987654321",
            country_code="91")
    ]

    user_id_with_phone_number_dtos = [
        UserIdWithPhoneNumberDTO(
            phone_number="1234567890",
            country_code="91",
            user_id="16fd2706-8baf-433b-82eb-8c7fada847da")
    ]

    UserAccount.objects.create(
        phone_number="1234567890", country_code="91",
        user_id="16fd2706-8baf-433b-82eb-8c7fada847da")
    user_accounts_storage_impl = UserAccountsSQLStorage()

    actual_response_dtos = user_accounts_storage_impl.get_user_ids_for_given_phone_numbers(
        phone_number_with_country_code_dtos=phone_number_with_country_code_dtos)

    assert actual_response_dtos == user_id_with_phone_number_dtos


