@pytest.mark.django_db
def test_create_user_account_phone_number_update_log_details():
    from ib_users.tests.factories import storage_dtos as storage_dtos_factories

    user_id = str(model_factories.UserAccountFactory().user_id)
    user_account_phone_dto = \
        storage_dtos_factories.UserAccountUpdatePhoneDetailsLogDTOFactory(
            user_id=user_id)

    user_accounts_storage_impl = UserAccountsSQLStorage()
    user_accounts_storage_impl.\
        create_user_account_phone_number_update_log_details(
            user_account_phone_dto)

    user_account_phone_number_log = \
        models.UpdateUserAccountPhoneNumberLog.objects.first()

    assert str(user_account_phone_number_log.user_account_id) == \
        user_account_phone_dto.user_id
    assert user_account_phone_number_log.new_phone_number == \
        user_account_phone_dto.new_phone_number
    assert user_account_phone_number_log.new_country_code == \
           user_account_phone_dto.new_country_code
    assert user_account_phone_number_log.old_phone_number == \
           user_account_phone_dto.old_phone_number
    assert user_account_phone_number_log.old_country_code == \
           user_account_phone_dto.old_country_code
    assert user_account_phone_number_log.change_reason == \
           user_account_phone_dto.change_reason
    assert user_account_phone_number_log.verification_status == \
           user_account_phone_dto.verification_status
    assert user_account_phone_number_log.failure_remarks == \
           user_account_phone_dto.failure_remarks


@pytest.mark.django_db
def test_create_user_account_phone_number_update_log_details():
    from ib_users.tests.factories import storage_dtos as storage_dtos_factories

    user_id = str(model_factories.UserAccountFactory().user_id)
    update_phone_number_log_obj = model_factories.UpdateUserAccountPhoneNumberLogFactory(
        user_account_id=user_id,
        verification_status=otp_verification_constants.VerificationStatus.VERIFIED.value)
    model_factories.UpdateUserAccountPhoneNumberLogFactory(
        user_account_id=user_id,
        verification_status=otp_verification_constants.VerificationStatus.VERIFICATION_IN_PROGRESS.value)

    update_user_account_phone_number_log_dtos = [
        storage_dtos_factories.UserAccountUpdatePhoneDetailsLogDTOFactory(
            user_id=str(update_phone_number_log_obj.user_account_id),
            old_phone_number=update_phone_number_log_obj.old_phone_number,
            old_country_code=update_phone_number_log_obj.old_country_code,
            new_phone_number=update_phone_number_log_obj.new_phone_number,
            new_country_code=update_phone_number_log_obj.new_country_code,
            change_reason=update_phone_number_log_obj.change_reason,
            verification_status=update_phone_number_log_obj.verification_status,
            failure_remarks=update_phone_number_log_obj.failure_remarks,
            verified_datetime=update_phone_number_log_obj.verified_datetime)
    ]

    user_accounts_storage_impl = UserAccountsSQLStorage()
    actual_response = user_accounts_storage_impl.get_users_phone_number_update_logs(
            user_ids=[user_id], verification_status=otp_verification_constants.VerificationStatus.VERIFIED.value)

    assert actual_response == update_user_account_phone_number_log_dtos


@pytest.mark.django_db
def test_get_users_accounts_details():
    # Arrange
    from ib_users.tests.factories import storage_dtos as storage_dtos_factories
    user_ids = [
        "abfc0184-1ef7-4d19-814b-d16d785b882a",
        "50a59b31-f015-4a61-9195-74478133cd17"
    ]

    user_account_objs = [
        model_factories.UserAccountFactory(
            user_id=user_ids[0]),
        model_factories.UserAccountFactory(
            user_id=user_ids[1])
    ]

    user_accounts_storage_impl = UserAccountsSQLStorage()
    expected_response = [
        storage_dtos_factories.UserAccountDTOFactory(
            user_id=user_ids[1],
            username=user_account_objs[1].username,
            email=user_account_objs[1].email,
            phone_number=user_account_objs[1].phone_number,
            country_code=user_account_objs[1].country_code
        ),
        storage_dtos_factories.UserAccountDTOFactory(
            user_id=user_ids[0],
            username=user_account_objs[0].username,
            email=user_account_objs[0].email,
            phone_number=user_account_objs[0].phone_number,
            country_code=user_account_objs[0].country_code
        )
    ]
    # Act
    actual_response = user_accounts_storage_impl.get_users_accounts_details_to_update(
        user_ids=user_ids)

    # Assert
    assert actual_response == expected_response
