    @pytest.mark.django_db
    def test_get_user_unique_login_ids(self):
        # arrange
        storage = UserLoginDeviceDetailsSQLStorage()

        user_account = UserAccount.objects.create(
            username="username34", password="password#$%")
        user_id = str(user_account.user_id)

        device_ids = ["Device1", "Device2"]
        UserUniqueLoginDevice.objects.create(user_account=user_account,
                                             device_id=device_ids[0])
        UserUniqueLoginDevice.objects.create(user_account=user_account,
                                             device_id=device_ids[1])

        # act
        actual_response = storage.get_user_unique_login_device_ids(user_id=user_id)

        # assert
        assert actual_response == device_ids

    @staticmethod
    def _create_user_login_device_and_get_dto():
        from ib_users.models import UserLoginDeviceDetails

        device_id = "device_id"
        user_account = UserAccountCreationForTestCases.create_user_with_username(
            username="username",
            password="password$%^")
        access_token = "access_token"
        UserLoginDeviceDetails.objects.create(device_id=device_id,
                                              user_account=user_account,
                                              access_token=access_token)
        user_logout_dto = UserDeviceLoginDTO(user_account.user_id,
                                             device_id,
                                             access_token)
        return user_logout_dto

    @pytest.mark.django_db
    def test_get_or_create_user_unique_login_device_when_user_with_device_not_exists(
            self, user_login_device_details_storage):
        # Arrange
        from ib_users.models import UserUniqueLoginDevice, UserAccount
        user_id = "c303282d-f2e6-46ca-a04a-35d3d873712d"
        device_id = "device_id"
        UserAccount.objects.create(user_id="c303282d-f2e6-46ca-a04a-35d3d873712d")

        # Act
        user_login_device_details_storage.get_or_create_user_unique_login_device(
            user_id=user_id, device_id=device_id)

        # Assert
        is_user_unique_device_exists = UserUniqueLoginDevice.objects.filter(
            user_account_id=user_id, device_id=device_id).exists()
        assert is_user_unique_device_exists is True

    @pytest.mark.django_db
    def test_get_or_create_user_unique_login_device_when_user_with_device_already_exists(
            self, user_login_device_details_storage):
        # Arrange
        from ib_users.models import UserUniqueLoginDevice, UserAccount
        user_id = "c303282d-f2e6-46ca-a04a-35d3d873712d"
        device_id = "device_id"
        UserAccount.objects.create(user_id="c303282d-f2e6-46ca-a04a-35d3d873712d")
        UserUniqueLoginDevice.objects.create(
            user_account_id=user_id, device_id=device_id)
        # Act
        user_login_device_details_storage.get_or_create_user_unique_login_device(
            user_id=user_id, device_id=device_id)

        # Assert
        is_user_unique_device_exists = UserUniqueLoginDevice.objects.filter(
            user_account_id=user_id, device_id=device_id).exists()
        assert is_user_unique_device_exists is True
