from ib_users.exceptions.custom_exception_constants import \
    ExceptionMessageDTO


class TestsUtils:

    @staticmethod
    def assert_custom_exception(expected_exception: ExceptionMessageDTO,
                                actual_exception):
        actual_exception = actual_exception.value
        assert actual_exception.error_message is expected_exception.message
        assert actual_exception.error_type is expected_exception.code